        _this.websocket.onerror = function () {
          //连接失败事件
          console.log("WebSocket连接发生错误"); //  this.reconnect();
        }; //接受信息


        _this.websocket.onmessage = function (event) {
          //接收服务器推送的信息
          //打印收到的服务器内容
          var data = JSON.parse(event.data);
          console.log(data); //开启心跳

          start();

          if (data.msgType == "kfNotice") {
            var List = [];
            List.push(data);
            console.log(data);

            _this.updataPushMapOverview(List);

            _this.openSuccess("系统推送一条新单");
          } else if (data.msgType == "groupText" || data.msgType == 'groupPic' || data.msgType == 'groupVoice' || data.msgType == 'groupMap') {
            if (data.groupID == _this.currentServiceInfo.channelID) {
              _this.chatMessageList.push(data);

              if (data.msgType == "groupMap") {
                data.msgObj.map = JSON.parse(data.msgObj.map);
                setTimeout(function () {
                  var map = new BMap.Map('groupMap' + data.msgTime); //

                  var point = new BMap.Point(data.msgObj.map.lng, data.msgObj.map.lat);
                  map.centerAndZoom(point, 19);
                  var marker = new BMap.Marker(point);
                  map.addOverlay(marker);
                }, 1);
              }

              _this.handleScroll();
            } else {// _this.innerLeftList.map(val)
            }
          } else if (data.msgType == "kfWelcome" || data.msgType == "kfEnd") {
            _this.chatMessageList.push(data);
          } else if (data.msgType == "kfApply") {
            var _iteratorNormalCompletion = true;
            var _didIteratorError = false;
            var _iteratorError = undefined;

            try {
              for (var _iterator = _this.innerLeftList[Symbol.iterator](), _step; !(_iteratorNormalCompletion = (_step = _iterator.next()).done); _iteratorNormalCompletion = true) {
                var it = _step.value;

                if (it.orderID == data.orderID) {
                  it.leftListCover = true;
                }
              }
            } catch (err) {
              _didIteratorError = true;
              _iteratorError = err;
            } finally {
              try {
                if (!_iteratorNormalCompletion && _iterator.return != null) {
                  _iterator.return();
                }
              } finally {
                if (_didIteratorError) {
                  throw _iteratorError;
                }
              }
            }
          } else if (data.msgType == "kfOrderSuccess") {
            setTimeout(function () {
              var div = document.getElementsByClassName('Overlay' + data.msgObj.orderID);
              div.remove(div);
            }, 50);
          }

          setTimeout(function () {
            _this.handleScroll();
          }, 50);
        }; //连接关闭


        _this.websocket.onclose = function (e) {
          clearTimeout(_this.timeoutObj);
          console.log("connection closed (" + e.code + ")"); //重连

          reconnect();
        };
      }

      function reconnect() {
        //重新连接
        if (_this.lockReconnect) {
          return;
        }

        _this.lockReconnect = true; //如果没有连上会一直重连，设置延迟避免请求过多

        _this.timeoutnum && clearTimeout(_this.timeoutnum);
        _this.timeoutnum = setTimeout(function () {
          // 新连接
          initWebSocket();
          _this.lockReconnect = false;
        }, false);
      }

      function start() {
        //开启心跳
        console.log('start  go');
        var time = 0;
        _this.timeoutObj = setInterval(function () {
          //这里发送一个心跳，后端收到后，返回一个心跳消息
          console.log('.11');
          var timeStamps = Date.parse(new Date()) / 1000;

          _this.websocket.send(JSON.stringify({
            opt: 'heart',
            accountID: _this.accountID,
            msgToken: _this.msgToken,
            timestamp: timeStamps,
            isKF: 1,
            gps: [{
              E: 121.30692,
              N: 31.16184,
              V: 0,
              D: -1,
              A: 13,
              T: timeStamps
            }]
          }));
        }, 30000);
      }
    }).catch(function (err) {
      console.log(err);
    }); //获取服务单列表

