    if(url&&url!==""){
      url.split('&').forEach(item=>{
        let query = item.split('=')
        sessionStorage.setItem(query[0],query[1])
      })
    }
    next()
  },
  created(){
    //获取个人信息
    this.ListData0={
        "status":"0",
